mapper.select:
SELECT *
FROM biddings 
WHERE user_id = #{user_id} AND item_id = #{item_id}

mapper.update:
UPDATE biddings 
SET is_win = 1 
WHERE item_id = #{item_id} 
AND price = (
    SELECT MAX(price) 
    FROM biddings 
    WHERE item_id = #{item_id}
)

UPDATE biddings
SET price = #{price}
WHERE user_id=#{user_id} AND item_id=#{item_id}
